import { useEffect, useState } from "react"

export default function Todo() {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const apiUrl = "http://localhost:8000"

    const handleSubmit = () => {
        setError("")
        if (title.trim() !== '' && description.trim() !== '') {
            fetch(apiUrl + "/todos", {
                method: "POST",
                headers: {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify({title, description})
            }).then((res) => {
                if (res.ok) {
                    setTodos([...todos, { title, description }])
                    setMessage("Item added Successfully")
                    setTimeout(() => {
                        setMessage("");
                    },3000)
                } else {
                    setError("Unable to create a Todo items")
                }
            }).catch(() => {
                setError("Unable to create a Todo items")
            })

        }
    }

    useEffect(() => {
        getItems()
    },[])

    const getItems = () => {
        fetch(apiUrl, "/todos")
            .then((res) => {res.json()
            })
            .then((res) => {
            setTodos(res)
        })
    }

    return <>
    <div className="row p-3 bg-success text-light">
        <h1>Todo Project with MERN Stack</h1>
        </div>

        <div className="row">
            <h3>Add Items</h3>
            {message && <p className="text-success">{message}</p> }
            <div className="form-group d-flex gap-2">
                <input placeholder="Title"  type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-control"/>
                <input placeholder="Description"  type="text" value={description} onChange={(e) => setDescription(e.target.value)}/>
                <button className="btn btn-dark" onClick={handleSubmit}>Submit</button>
            </div>
            {error && <p className="text-danger">{error}</p>}
        </div>

        <div className="row mt-3">
            <h3>Tasks</h3>
            <ul className="list-group">
                <li className="list-group-item  bg-info d-flex justify-content-between align-items-center my-2">
                    <div className="d-flex flex-column">
                        <span className="fw-bold">Item Title</span>
                        <span> Item Desc</span>
                    </div>

                    <div className="d-flex gap-2">
                        <button className="btn btn-warning">Edit</button>
                        <button className="btn btn-danger">Delete</button>
                    </div>
                </li>

                <li className="list-group-item  bg-info d-flex justify-content-between align-items-center my-2">
                    <div className="d-flex flex-column">
                        <span className="fw-bold">Item Title</span>
                        <span> Item Desc</span>
                    </div>

                    <div className="d-flex gap-2">
                        <button className="btn btn-warning">Edit</button>
                        <button className="btn btn-danger">Delete</button>
                    </div>
                </li>
            </ul>
        </div>
        </>
}
