    return (
        <>
            <div className="row p-4 bg-success text-light mb-4">
                <h1 className="text-center">Todo Project with MERN Stack</h1>
            </div>

            <div className="container">
                <div className="row mb-4">
                    <h3>Add Items</h3>
                    {message && <p className="text-success">{message}</p>}
                    <div className="form-group d-flex flex-column flex-sm-row gap-3">
                        <input
                            placeholder="Title"
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            className="form-control"
                        />
                        <input
                            placeholder="Description"
                            className="form-control"
                            type="text"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                        <button
                            className="btn btn-dark"
                            onClick={handleSubmit}
                        >
                            Submit
                        </button>
                    </div>
                    {error && <p className="text-danger mt-2">{error}</p>}
                </div>

                <div className="row">
                    <h3>Tasks</h3>
                    <div className="col-md-8 mx-auto">
                        <ul className="list-group">
                            {todos.map((item) => (
                                <li
                                    key={item._id}
                                    className="list-group-item bg-info text-light d-flex justify-content-between align-items-center my-2 rounded"
                                >
                                    <div className="d-flex flex-column me-3">
                                        {editId === -1 || editId !== item._id ? (
                                            <>
                                                <span className="fw-bold">{item.title}</span>
                                                <span>{item.description}</span>
                                            </>
                                        ) : (
                                            <div className="form-group d-flex flex-column gap-2">
                                                <input
                                                    placeholder="Title"
                                                    onChange={(e) => setEditTitle(e.target.value)}
                                                    value={editTitle}
                                                    className="form-control"
                                                    type="text"
                                                />
                                                <input
                                                    placeholder="Description"
                                                    onChange={(e) => setEditDescription(e.target.value)}
                                                    value={editDescription}
                                                    className="form-control"
                                                    type="text"
                                                />
                                            </div>
                                        )}
                                    </div>
                                    <div className="d-flex gap-2">
                                        {editId === -1 || editId !== item._id ? (
                                            <>
                                                <button
                                                    className="btn btn-warning"
                                                    onClick={() => handleEdit(item)}
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    className="btn btn-danger"
                                                    onClick={() => handleDelete(item._id)}
                                                >
                                                    Delete
                                                </button>
                                            </>
                                        ) : (
                                            <>
                                                <button
                                                    className="btn btn-warning"
                                                    onClick={handleUpdate}
                                                >
                                                    Update
                                                </button>
                                                <button
                                                    className="btn btn-secondary"
                                                    onClick={handleEditCancel}
                                                >
                                                    Cancel
                                                </button>
                                            </>
                                        )}
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </>
    );
}
