import { useEffect, useState } from "react"

export default function Todo() {

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const [editId, setEditId] = useState(-1);

    const [editTitle, setEditTitle] = useState("");
    const [editDescription, setEditDescription] = useState("");

    const apiUrl = "http://localhost:8000"

    const handleSubmit = () => {
        setError("")
        if (title.trim() !== '' && description.trim() !== '') {
            fetch(apiUrl + "/todos", {
                method: "POST",
                headers: {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify({title, description})
            }).then((res) => {
                if (res.ok) {
                    setTodos([...todos, { title, description }])
                    setMessage("Item added Successfully")
                    setTimeout(() => {
                        setMessage("");
                    },3000)
                } else {
                    setError("Unable to create a Todo items")
                }
            }).catch(() => {
                setError("Unable to create a Todo items")
            })

        }
    }

    useEffect(() => {
        getItems()
    },[])

    const getItems = () => {
        fetch(apiUrl + "/todos")
            .then((res) =>  res.json())
            .then((res) => {
                setTodos(res)
        })
    }

    const handleEdit = (item) => {
        setEditId(item._id);
        setEditTitle(item.title);
        setEditDescription(item.description);
    }

    const handleEditCancel = () => {
        setEditId(-1)
    }

    const handleUpdate = () => {
        setError("")
        if (editTitle.trim() !== '' && editDescription.trim() !== '') {
            fetch(apiUrl + "/todos/" + editId, {
                method: "PUT",
                headers: {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify({title: editTitle, description: editDescription})
            }).then((res) => {
                if (res.ok) {
                    // eslint-disable-next-line array-callback-return
                    todos.map((item) => {
                        if (item.id === editId) {
                            item.title = editTitle;
                            item.description = editDescription;
                        }
                    })
                    setTodos([...todos, { title, description }])
                    setMessage("Item added Successfully")
                    setTimeout(() => {
                        setMessage("");
                    },3000)
                } else {
                    setError("Unable to create a Todo items")
                }
            }).catch(() => {
                setError("Unable to create a Todo items")
            })

        }
    }

    return <>
    <div className="row p-3 bg-success text-light">
        <h1>Todo Project with MERN Stack</h1>
        </div>

        <div className="row">  
            <h3>Add Items</h3>
            {message && <p className="text-success">{message}</p> }
            <div className="form-group d-flex gap-2">
                <input placeholder="Title"  type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-control"/>
                <input placeholder="Description" className="form-control" type="text" value={description} onChange={(e) => setDescription(e.target.value)}/>
                <button className="btn btn-dark" onClick={handleSubmit}>Submit</button>
            </div>
            {error && <p className="text-danger">{error}</p>}
        </div>

        <div className="row mt-3">
            <h3>Tasks</h3>
            <ul className="list-group">
                {
                    todos.map((item) =>
                        <li className="list-group-item  bg-info d-flex justify-content-between align-items-center my-2">
                            <div className="d-flex flex-column me-2">
                                {
                                    editId === -1 || editId !== item._id ? <>
                                        <span className="fw-bold">{ item.title}</span>
                                        <span>{ item.description}</span>
                                    </> : <>
                                        <div className="form-group d-flex gap-2">
                                          <input placeholder="Title"  onChange={(e) => setEditTitle(e.target.value)} value={editTitle}  className="form-control" type="text"/>
                                          <input placeholder="Description"  onChange={(e) => setEditDescription(e.target.value)} value={editDescription} className="form-control" type="text"/>
                                        </div>  
                                    </>
                                }

                        </div>

                            <div className="d-flex gap-2">
                                {
                                    editId === -1 || editId !== item._id ?
                                        <button className="btn btn-warning" onClick={() => handleEdit(item)}>Edit</button> : <button  className = "btn btn-warning" onChange={(handleUpdate)}>Update</button>
                                }
                                { editId === -1 ?<button className="btn btn-danger">Delete</button> :
                                    <button className="btn btn-danger" onClick={handleEditCancel}>Cancel</button>
                                }
                        </div>
                        </li>
                    )}
            </ul>
        </div>
        </>
}
